	@Override
	public boolean login(String mail, String password, List<Customer> customers) {
		boolean loginOk = false;
		Customer cstrm = null;
		for (Customer customer : customers) {
			if (customer.getEmail()==mail&&customer.getPassword()==password) {
				loginOk = true;
				cstrm = customer;
			}
		}
		if (loginOk) {
			System.out.println("Giriþ Baþarýlý Merhaba "+ cstrm.getFirstName());
		}
		else {
			System.out.println("Bilgilerinizi Tekrar Kontorl Ediniz");
		}
		return loginOk;
	}

